package com.xworkz.external;

public class Bag {
    public Bag() {
        System.out.println("Bag is the superclass");
    }

    public void storeItems() {
        System.out.println("Storing items in the bag...");
    }

    public void carryBag() {
        System.out.println("Carrying the bag...");
    }

    public void zipBag() {
        System.out.println("Zipping the bag...");
    }

    public void openBag() {
        System.out.println("Opening the bag...");
    }

    public void checkCapacity() {
        System.out.println("Checking bag capacity...");
    }
}
