package com.xworkz.external;

public class Bottle {
    public Bottle() {
        System.out.println("Bottle is the superclass");
    }

    public void fill() {
        System.out.println("Filling the bottle with water");
    }

    public void drink() {
        System.out.println("Drinking water from the bottle");
    }

    public void clean() {
        System.out.println("Cleaning the bottle");
    }

    public void close() {
        System.out.println("Closing the bottle cap");
    }

    public void carry() {
        System.out.println("Carrying the bottle");
    }

}
