package com.xworkz.external;

public class Park {
    public Park() {
        System.out.println("Park is the superclass");
    }

    public void openPark() {
        System.out.println("Park is open for visitors");
    }

    public void provideWalkingTrack() {
        System.out.println("Park has a walking track for fitness enthusiasts");
    }

    public void maintainGreenery() {
        System.out.println("Park is well-maintained with trees and gardens");
    }

    public void provideSeatingArea() {
        System.out.println("Park has benches and seating areas for relaxation");
    }

    public void closePark() {
        System.out.println("Park closes in the evening");
    }
}
